        public class CloseWebApp : Function<Ok>
        {
            /// <summary>
            /// Data type for serialization
            /// </summary>
            [JsonProperty("@type")]
            public override string DataType { get; set; } = "closeWebApp";

            /// <summary>
            /// Extra data attached to the function
            /// </summary>
            [JsonProperty("@extra")]
            public override string Extra { get; set; }

            /// <summary>
            /// Identifier of Web App launch, received from openWebApp
            /// </summary>
            [JsonConverter(typeof(Converter.Int64))]
            [JsonProperty("web_app_launch_id")]
            public long WebAppLaunchId { get; set; }
        }

        /// <summary>
        /// Informs TDLib that a previously opened Web App was closed
        /// </summary>
        public static Task<Ok> CloseWebAppAsync(
            this Client client, long webAppLaunchId = default)
        {
            return client.ExecuteAsync(new CloseWebApp
            {
                WebAppLaunchId = webAppLaunchId
            });
        }
